function convertHzToKPa(input_frequency) {
    if (input_frequency > 6430) return 0;
    if (input_frequency >= 4330 && input_frequency <= 6430) return 9 - (input_frequency - 4330) * 0.004286;
    if (input_frequency >= 2820 && input_frequency <= 4330) return 15 - (input_frequency - 2820) * 0.003974;
    if (input_frequency >= 1110 && input_frequency <= 2820) return 35 - (input_frequency - 1110) * 0.01170;
    if (input_frequency >= 770 && input_frequency <= 1110) return 55 - (input_frequency - 770) * 0.05884;
    if (input_frequency >= 600 && input_frequency <= 770) return 75 - (input_frequency - 600) * 0.1176;
    if (input_frequency >= 485 && input_frequency <= 600) return 100 - (input_frequency - 485) * 0.2174;
    if (input_frequency >= 293 && input_frequency <= 485) return 200 - (input_frequency - 293) * 0.5208;
    if (input_frequency < 293) return 200;
    return null;
}

function processAndStore(msg) {
    try {
        const data = msg.payload;
        const now = new Date();

        // Convert both frequency readings to kPa
        const watermark1 = convertHzToKPa(data.object.input5_frequency);
        const watermark2 = convertHzToKPa(data.object.input6_frequency);

        // Create formatted data structure
        const formattedData = {
            timestamp: now.toISOString(),
            readings: {
                input5_frequency: data.object.input5_frequency,
                watermark1: watermark1,
                input6_frequency: data.object.input6_frequency,
                watermark2: watermark2,
                light_intensity: data.object.light_intensity || null
            }
        };

        // Create daily JSON file
        const fileName = "/data/irrigation/kiwi_" + now.getFullYear() + "-" +
            (now.getMonth() + 1).toString().padStart(2, '0') + "-" +
            now.getDate().toString().padStart(2, '0') + ".json";

        // Update node status with both moisture readings
        node.status({
            fill: "blue",
            shape: "dot",
            text: "WM1: " + watermark1.toFixed(2) + " kPa, WM2: " + watermark2.toFixed(2) + " kPa"
        });

        // Prepare message for file operations
        msg.formattedData = formattedData;
        msg.filename = fileName;

        return msg;

    } catch (error) {
        node.error("Error processing message: " + error.message);
        return null;
    }
}

return processAndStore(msg);

