// Debug incoming message
node.log("Received message - Topic: " + msg.topic + ", Payload: " + msg.payload);

let settings = global.get('irrigationSettings');

// Ensure irrigation object exists
if (!settings.irrigation) {
    settings.irrigation = {
        times: [],
        duration: 20
    };
}

// Validate numeric inputs (trigger, depth, duration)
if (msg.topic === 'WM1_trigger' || msg.topic === 'WM2_trigger' ||
    msg.topic === 'WM1_depth' || msg.topic === 'WM2_depth' ||
    msg.topic === 'irrigation_duration') {

    let value = Number(msg.payload);
    if (isNaN(value)) {
        node.status({ fill: "red", shape: "ring", text: "Not a number: " + msg.payload });
        return null;
    }

    // Check ranges
    if (msg.topic.includes('trigger')) {
        if (value < 0 || value > 200) {
            node.status({ fill: "red", shape: "ring", text: "Trigger out of range (0-200): " + value });
            return null;
        }
    } else if (msg.topic.includes('depth')) {
        if (value < 0 || value > 500) {
            node.status({ fill: "red", shape: "ring", text: "Depth out of range (0-100): " + value });
            return null;
        }
    } else if (msg.topic === 'irrigation_duration') {
        if (value < 1 || value > 120) {
            node.status({ fill: "red", shape: "ring", text: "Duration out of range (1-60): " + value });
            return null;
        }
    }
    msg.payload = Math.round(value);
}

// Validate irrigation time
if (msg.topic === 'add_irrigation_time') {
    // Debug log
    node.log("Time validation - Received: '" + msg.payload + "' Type: " + typeof msg.payload);

    const timeString = String(msg.payload).trim();

    // Time format regex: accepts "HH:mm" or "H:mm"
    const timeRegex = /^([0-9]|0[0-9]|1[0-9]|2[0-3]):([0-5][0-9])$/;

    if (!timeRegex.test(timeString)) {
        node.log("Time validation failed for: " + timeString);
        return null;
    }

    // Format time to ensure consistent HH:mm format
    const [hours, minutes] = timeString.split(':');
    msg.payload = `${hours.padStart(2, '0')}:${minutes}`;
    node.log("Time validation passed: " + msg.payload);
}

// Update settings based on which input was changed
switch (msg.topic) {
    case 'WM1_enable':
        settings.watermark1.enabled = Boolean(msg.payload);
        node.status({ fill: "blue", shape: "dot", text: "WM1 Installed: " + msg.payload });
        break;
    case 'WM2_enable':
        settings.watermark2.enabled = Boolean(msg.payload);
        node.status({ fill: "blue", shape: "dot", text: "WM2 Installed: " + msg.payload });
        break;
    case 'WM1_trigger':
        settings.watermark1.trigger = msg.payload;
        node.status({ fill: "blue", shape: "dot", text: "WM1 Trigger: " + msg.payload + " kPa" });
        break;
    case 'WM2_trigger':
        settings.watermark2.trigger = msg.payload;
        node.status({ fill: "blue", shape: "dot", text: "WM2 Trigger: " + msg.payload + " kPa" });
        break;
    case 'WM1_depth':
        settings.watermark1.depth = msg.payload;
        node.status({ fill: "blue", shape: "dot", text: "WM1 Depth: " + msg.payload + " cm" });
        break;
    case 'WM2_depth':
        settings.watermark2.depth = msg.payload;
        node.status({ fill: "blue", shape: "dot", text: "WM2 Depth: " + msg.payload + " cm" });
        break;
    case 'add_irrigation_time':
        if (!settings.irrigation.times.includes(msg.payload)) {
            settings.irrigation.times.push(msg.payload);
            settings.irrigation.times.sort();
            node.status({ fill: "blue", shape: "dot", text: "Added time: " + msg.payload });

            // Save settings
            global.set('irrigationSettings', settings);

            // Return both template update and schedule update
            return [
                {  // First output: for template and debug
                    topic: msg.topic,
                    payload: settings,
                    lastChanged: {
                        topic: msg.topic,
                        value: msg.payload,
                        time: new Date().toISOString()
                    }
                },
                {  // Second output: for file save
                    payload: JSON.stringify(settings, null, 2),
                    filename: '/data/irrigation/settings.json'
                }
            ];
        }
        break;

    case 'delete_irrigation_time':
        // Save settings
        global.set('irrigationSettings', msg.payload);
        node.status({ fill: "blue", shape: "dot", text: "Updated times" });

        return [
           {  // First output: for template and debug
                topic: delete_irrigation_time,
               payload: msg.payload,
               lastChanged: msg.lastChanged
           },
            {  // Second output: for file save
                payload: JSON.stringify(msg.payload, null, 2),
                filename: '/data/irrigation/settings.json'
            }
        ];
        break;
    default:
        node.status({ fill: "yellow", shape: "ring", text: "Unknown topic: " + msg.topic });
        return null;
}

// Store updated settings
global.set('irrigationSettings', settings);

// Return array of messages
return [
    {  // First output: for template and debug
        topic: msg.topic,
        payload: settings,
        lastChanged: {
            topic: msg.topic,
            value: msg.payload,
            time: new Date().toISOString()
        }
    },
    {  // Second output: for file save
        payload: JSON.stringify(settings, null, 2),
        filename: '/data/irrigation/settings.json'
    }
];

