function generateTestData(days = 7) {
    let watermark1 = [];
    let watermark2 = [];
    let dates = [];

    // Generate one point per day
    for (let i = 0; i < days; i++) {
        const date = new Date();
        date.setDate(date.getDate() - (days - i - 1));
        const dateStr = (date.getMonth() + 1).toString().padStart(2, '0') + 
                       '/' + date.getDate().toString().padStart(2, '0');

        // Create repeating pattern every 3 days
        const dayInCycle = i % 3;

        let value1, value2;
        if (dayInCycle === 0) {  // First day
            value1 = 15;
            value2 = 15;
        } else if (dayInCycle === 1) {  // Second day
            value1 = 45;
            value2 = 35;
        } else {  // Third day
            value1 = 80;
            value2 = 60;
        }

        // Add small random variation
        watermark1.push(Number(value1 + (Math.random() * 4 - 2)));
        watermark2.push(Number(value2 + (Math.random() * 4 - 2)));
        dates.push(dateStr);
    }

    msg.payload = [{
        series: ["Watermark 1", "Watermark 2"],
        data: [watermark1, watermark2],
        labels: dates,
        options: {
            scales: {
                yAxes: [{
                    scaleLabel: {
                        display: true,
                        labelString: 'Soil Moisture Tension (kPa)'
                    }
                }]
            }
        }
    }];

    return msg;
}

return generateTestData(7);

let times = msg.payload.irrigation.times;
if (!times || !Array.isArray(times)) {
    node.warn("No valid times array found");
    return null;
}

// Format for cron-plus dynamic schedules
let schedules = {
    schedules: times.map(time => {
        let [hours, minutes] = time.split(':');
        return {
            name: `irrigation_${time}`,
            topic: "irrigation_check",
            payload: {
                time: time,
                type: "irrigation_check"
            },
            expression: `${minutes} ${hours} * * *`,
            expressionType: "cron",
            payloadType: "json",
            enabled: true
        };
    })
};

return {
    topic: "dynamic-schedules",
    payload: schedules
};

if (msg.topic === "irrigation_check") {
    // Log the triggered schedule
    node.log("Irrigation schedule triggered at: " + new Date().toLocaleTimeString());
    node.log("Schedule details: " + JSON.stringify(msg.payload));

    // Create status message
    node.status({
        fill: "green",
        shape: "dot",
        text: "Triggered: " + msg.payload.time
    });

    return msg;
}
return null;

let activeSchedules = [];

if (msg.topic === "dynamic-schedules") {
    // New schedules being set
    activeSchedules = msg.payload.schedules.map(s => ({
        time: s.payload.time,
        expression: s.expression,
        enabled: s.enabled
    }));
} else if (msg.topic === "irrigation_check") {
    // Schedule triggered
    activeSchedules.push({
        time: msg.payload.time,
        lastTriggered: new Date().toLocaleTimeString()
    });
}

// Create monitoring message for debug/dashboard
return {
    topic: "schedule_status",
    payload: {
        activeSchedules: activeSchedules,
        lastUpdated: new Date().toLocaleTimeString(),
        totalSchedules: activeSchedules.length
    }
};
